import { Component, OnInit } from '@angular/core';
import { AppServiceService } from '../app-service.service';
import { Subscription } from 'rxjs';
import { DataModel, sortByCarrier, sortByCarrierDesc, sortByDate, sortByDateDesc, sortByIccid, sortById,sortByIdDesc, sortByIdentity, sortByMsisdn } from '../app.models';
import { Router } from '@angular/router';

@Component({
  selector: 'app-list-component',
  templateUrl: './list-component.component.html',
  styleUrls: ['./list-component.component.scss']
})
export class ListComponentComponent implements OnInit {

  public listData:any[] =[];
  public data:DataModel[]=[];
  public totalPages=0;
  public currentPage=1;
  public nextPage=0;
  public displayPerPage=5;
  public currentData:DataModel[]=[];
  public subsScriptions:Subscription[]=[];
  public sortBy:any;
  public noOfPages=0;
  public currentPage1:any;
  public currentPage2:any;
  public currentPage3:any ;
  public currentPage4:any;
  public currentPage5:any=-1

  public editableBooleans ={
    idIsUp:false,
    dateActivateIsUp:false,
    careerIsUp:false

  }


  constructor(private appService:AppServiceService,private router:Router){

  }

  public ngOnInit(): void {
    this.initializeData();
  }

  public initializeData():void{
    this.subsScriptions.push(this.appService.getListData().subscribe(x=>{
      this.data=x;
      this.assignPageData();
    }));
    this.appService.currentPage.subscribe(x=>{
      if(x){
        this.currentPage=x;
        this.assignPageData();
      }
    })
  }
  
  public calculatePageNumber():void{
    this.noOfPages = Math.ceil(this.data?.length/this.displayPerPage);
  }
  public assignPageData():void{
    this.currentData=[]
    Object.assign(this.currentData , this.data.slice(0,this.displayPerPage));
    this.calculatePageNumber();
    this.calculateCurrentPages();
    this.displayDataPerPageNumber();
  }

  calculateCurrentPages():void{
    if(this.currentPage5<this.currentPage){
    this.currentPage1 = this.currentPage;
    this.currentPage2= this.currentPage+1;
    this.currentPage3 = this.currentPage+2;
    this.currentPage4 = this.currentPage+3;
    this.currentPage5 = this.currentPage+4;
    } else if(this.currentPage1>this.currentPage){
      this.currentPage1 = this.currentPage;
      this.currentPage2= this.currentPage+1;
      this.currentPage3 = this.currentPage+2;
      this.currentPage4 = this.currentPage+3;
      this.currentPage5 = this.currentPage+4;
    }
  }

  public displayDataPerPageNumber():void{
    this.currentData=[]
    if(this.currentPage)
    Object.assign(this.currentData,this.data.slice((this.currentPage-1)*(this.displayPerPage-1), (this.currentPage-1)*(this.displayPerPage-1)+this.displayPerPage))
  }

  public nextPageNavigate():void{
    this.currentPage++;
    this.displayDataPerPageNumber();
  }
  public previousPage():void{
    this.currentPage--;
    this.displayDataPerPageNumber();
  }

  public carrierIcon(carrierName:string):string{
    let iconLink="";
    switch(carrierName){
      case 'vodafone' :iconLink="data:image/png;base64,";
      break;
      case 'airtel': iconLink="https://play-lh.googleusercontent.com/uFg3zOsnGZkIrswmvXyFYhoF3gC4tv0ovFZv0zisJFQ2DZqJyh9SUGrK6D-Tnn1lGqc";
      break;
      case 'idea': iconLink="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYyr5CXpDFxgcUaHPUl-H-GKYoUD-Uf38t7g&usqp=CAU";
      break;
      case 'jio': iconLink="data:image/png;base64,";
      break;
      case 'tata docomo': iconLink="data:image/jpeg;base64,";
      break;
      default : iconLink="";

    }
    return iconLink;
  }

  public optionsPerPage(event:any):void{
    this.displayPerPage= Number(event);
    this.assignPageData();
  }

  public ngOnDestroy():void{
    this.subsScriptions.forEach(subscription=>{
      subscription.unsubscribe();
    })
  }

  public sortDataBy(event:string):void{
    switch(event){
      case 'carrier':  this.data =this.data.sort(sortByCarrier);
      break;
      case 'iccid': this.data = this.data.sort(sortByIccid);
      break;
      case 'msisdn': this.data = this.data.sort(sortByMsisdn);
      break;
      case 'id': this.data = this.data.sort(sortById);
      break;
      case 'identity': this.data = this.data.sort(sortByIdentity);
      break;
    }
    this.displayDataPerPageNumber();
  }

  public navigateFirst(page?:number):void{
    this.currentPage=1;
    this.assignPageData();
  }
  public navigateNext(page?:number):void{
    this.currentPage++;
    this.assignPageData();
  }
  public navigatePrevious(page?:number):void{
    this.currentPage--;
    this.assignPageData();
  }
  public navigateLast(page?:number):void{
    this.currentPage = this.noOfPages;
    this.assignPageData();
  }

  public setCurrentPage(page:number):void{
    this.currentPage=page;
    this.assignPageData();
  }
   
 
  public loadRecord(i:any):void{
    this.appService.currentPage.next(this.currentPage);
    this.router.navigateByUrl(`/record?id=${i}`)
  }

  getDate(input:any):Date{
    return new Date(input);
  }

  public isIdUpIconClickAction():void{
    this.editableBooleans.idIsUp=true;
    this.currentData.sort(sortByIdDesc);
  }
  public isIdDownIconClickAction():void{
    this.editableBooleans.idIsUp=false;
    this.currentData.sort(sortById)
  }

  public isdateActiveIconDownClickAction():void{
    this.editableBooleans.careerIsUp=false;
    this.currentData.sort(sortByIdDesc);
  }

  public isdateActivateUpIconClickAction():void{
    this.editableBooleans.careerIsUp=true;
    this.currentData.sort(sortByIdDesc);
  }

  public isCareerIconDownClickAction():void{
    this.editableBooleans.careerIsUp=false;
    this.currentData.sort(sortByCarrier);
  }

  public isCareerUpIconClickAction():void{
    this.editableBooleans.careerIsUp=true;
    this.currentData.sort(sortByCarrierDesc);
  }

  public dateIconUpClick():void{
    this.editableBooleans.dateActivateIsUp=true;
    this.currentData.sort(sortByDate);
  }
  public dateIconDOwnClick():void{
    this.editableBooleans.dateActivateIsUp=false;
    this.currentData.sort(sortByDateDesc);
  }
}
